/* React */
import React from 'react' ;
import axios from 'axios';
import MiniModalLeft from './components/MiniModalLeft';
import MiniModalRight from './components/MiniModalRight';
import { useState, useEffect } from 'react';
import {useNavigate } from 'react-router-dom';

/* Images*/
import register from '../Images/Register.svg'
import logo from '../Icons/register-logo.svg'

/*Icons*/
import Okay from '../Icons/okay.svg';
import NotOkay from '../Icons/notOkay.svg';

const Register =() => {

    let navigate = useNavigate();

    const [data, setData] = useState({
        name:'',
        surname:'',
        age:'',
        gender:'',
        phone:'',
        email:'',
        password:'',
        passwordCon:'',
        image:'',
    });

    const [nameError, setNameError] = useState();
    const [lastError, setLastError] = useState();
    const [emailError, setEmailError] = useState();
    const [usernameError, setUsernameError] = useState();
    const [contactError, setContactError] = useState();
    const [passwordError, setPasswordError] = useState();
    const [passwordConError, setPasswordConError] = useState();

    const [emailAvail, setEmailAvail] = useState();
    const [userAvail, setUserAvail] = useState();

    const [emailIcon, setEmailIcon] = useState();
    const [userIcon, setUserIcon] = useState();

    const firstVal = (e) => {
        const value = e.target.value;
        setInputs({...inputs, first: value});
        if(inputs.first !== ''){setNameError();} 
    }

    const imageVal = (e) => {           
            let file = e.target.files[0];
            let reader = new FileReader();

            reader.onloadend = function() {
            console.log(reader.result);
            let imgFile = reader.result;

            setInputs({...inputs, image: imgFile});

            let image = new Image();
            image.src = reader.result;
            document.getElementById('profileimg').appendChild(image);

            }
            reader.readAsDataURL(file);
    }

    const lastVal = (e) => {
        const value = e.target.value;
        setInputs({...inputs, last: value});
        if(inputs.last !== ''){setLastError();} 
    }

    const emailVal = (e) => {
        const mailcodex = /^(([^<>()[\]\\.,;:\s@"]+(\.[^<>()[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/;
        const value = e.target.value;
        setInputs({...inputs, email: value});
        if(inputs.email !== ''){
            setEmailError();
        } 
        if(!value.match(mailcodex)){
            setEmailError(<MiniModalLeft message="Email is not a valid format" />);
        }    
    }

    const validateEmail = () => {
        axios.post('http://localhost:8888/api/authenticateEmail.php', inputs)
        .then(function(response){
         console.log(response);
         if(response.data === "Available"){
            setEmailIcon(Okay);
            setEmailAvail();
         } else if(response.data === "Not Available") {
            setEmailAvail(<MiniModalRight message="Email Is Not Available" />);
            setEmailIcon(NotOkay);
         } else if(response.data === "") {
            setEmailIcon();
            setEmailAvail();
            setEmailError();
         }
        });
    }

    const usernameVal = (e) => {
        const value = e.target.value.trim();
        setInputs({...inputs, username: value});
        if(inputs.username != ''){setUsernameError();} 
    }

    const validateUser = () => {
        axios.post('http://localhost:8888/api/authenticateUser.php', inputs)
        .then(function(response){
         console.log(response);
         if(response.data === "Available"){
            setUserAvail();
            setUserIcon(Okay);
         } else {
            setUserAvail(<MiniModalLeft message="Username Is Not Available" />);
            setUserIcon(NotOkay);
         }
        });
    }

    const contactVal = (e) => {
        const contCodex = /^\(?(\d{3})\)?[- ]?(\d{3})[- ]?(\d{4})$/;
        const value = e.target.value;
        setInputs({...inputs, contact: value});
        if(inputs.contact != ''){setContactError();} 

        if(!value.match(contCodex)){
            setContactError(<MiniModalRight message="Not a Valid Phone Number" />);
        } 
    }

    const passwordVal = (e) => {
        const passCodex = /^(?=.*[a-z])(?=.*[A-Z])(?=.*\d)(?=.*(\W|_)).{5,}$/ ;
        const value = e.target.value;
        setInputs({...inputs, password: value});
        if(inputs.password != ''){setPasswordError();} 

        if(!value.match(passCodex)){
            setPasswordError(<MiniModalLeft message="Password must include stuff" />);
        } 
    }

    const passwordConVal = (e) => {
        const value = e.target.value;
        setInputs({...inputs, passwordCon: value});
        if(inputs.password === value){setPasswordConError()}else{
            setPasswordConError(<MiniModalLeft message="Your Passwords Dont Match" />);
        }  
    }



    const handleChange = (e) =>{
        setData({...data, [e.target.name]: e.target.value});
        //console.log(data)
    }

    const submitForm=(e) =>{
        e.preventDefualt();
    const sendData = {
            name:data.name,
            surname:data.surname,
            age:data.age,
            gender:data.gender,
            phone:data.phone,
            email:data.email,
            password:data.password,
            passwordCon:data.passwordCon,

        }
        console.log(sendData);

        axios.post('http://localhost:8888/surgicalApi/Receptionist.php',sendData)
        .then((result)=>{
            if (result.data.Status == 'Invalid'){
                alert('Invalid User');
            }

            else {
                navigate(`/Login`);

            }
        })
    }






    return (
        <div className='register-page'>
             <img className ="register-img" src = {register}></img>
             <img className ="register-logo" src = {logo}></img>
             <h2 className= "Side-text">urgical Society</h2>
             <p className="slogan">Building a healthy community one individual at a time.</p>


            <div className= 'register-form'>
                <form onSubmit={submitForm}>
                    <h2 className= "R-text">Register</h2>
                    <p className="Rsub-text">Already have an account?</p>
                    <a href='./Login' ><p className='login-link' >Login</p></a>

                    <p className="profile-text">Select your profile picture</p>

                    <div className= 'profile'>

                        </div>

                        <input name= 'name'type="text" placeholder='Name' onChange={handleChange} value={data.name}/>
                        <input name= 'surname'type="text" placeholder='Surname' onChange={handleChange} value={data.surname}/>
                        <input name= 'age' type="text" placeholder='Age' onChange={handleChange} value={data.age}/>

                        <select name="gender" defualtvalue="none" onChange={handleChange} value={data.gender}>
                            <option value="none">Gender</option>
                            <option value="male">Male</option>
                            <option value="female">Female</option>
                            <option value="other">Other</option>
                        </select>

                        <input name= 'phone' type="text" placeholder='Phone number' onChange={handleChange} value={data.phone}/>
                        <input name= "email" type="text" placeholder='Email' onChange={handleChange} value={data.email}/>
                        <input name= "password" type="text" placeholder='Password'onChange={handleChange} value={data.password} />
                        <input name= "passwordCon" type="text" placeholder='Confirm Password' onChange={handleChange} value={data.passwordCon} />

                        <button type='submit'>Register User</button>
                </form>
            </div>
        </div>
    )
}

export default Register
