/* React */
import React from 'react' ;
import axios from 'axios';
import { useState, useEffect } from 'react';
import {useNavigate } from 'react-router-dom';

/* Images*/
import register from '../Images/Register.svg'
import logo from '../Icons/register-logo.svg'

const Register =() => {

    let navigate = useNavigate();

    const [data, setData] = useState({
        name:'',
        surname:'',
        age:'',
        gender:'',
        phone:'',
        email:'',
        password:'',
        passwordCon:'',
    });

    const handleChange = (e) =>{
        setData({...data, [e.target.name]: e.target.value});
        //console.log(data)
    }

    const submitForm=(e) =>{
        e.preventDefualt();
    const sendData = {
            name:data.name,
            surname:data.surname,
            age:data.age,
            gender:data.gender,
            phone:data.phone,
            email:data.email,
            password:data.password,
            passwordCon:data.passwordCon,

        }
        console.log(sendData);

        axios.post('http://localhost/surgicalApi/insert.php',sendData)
        .then((result)=>{
            if (result.data.Status == 'Invalid'){
                alert('Invalid User');
            }

            else {
                navigate(`/Login`);

            }
        })
    }






    return (
        <div className='register-page'>
             <img className ="register-img" src = {register}></img>
             <img className ="register-logo" src = {logo}></img>

            <div className= 'register-form'>
                <form onSubmit={submitForm}>
                    <h2>Register now!</h2>

                    <p className="profile-text">Select your profile picture</p>

                    <div className= 'profile'>

                        </div>

                        <input name= 'name'type="text" placeholder='Name' onChange={handleChange} value={data.name}/>
                        <input name= 'surname'type="text" placeholder='Surname' onChange={handleChange} value={data.surname}/>
                        <input name= 'age' type="text" placeholder='Age' onChange={handleChange} value={data.age}/>

                        <select name="gender" defualtvalue="none" onChange={handleChange} value={data.gender}>
                            <option value="none">Gender</option>
                            <option value="male">Male</option>
                            <option value="female">Female</option>
                            <option value="other">Other</option>
                        </select>

                        <input name= 'phone' type="text" placeholder='Phone number' onChange={handleChange} value={data.phone}/>
                        <input name= "email" type="text" placeholder='Email' onChange={handleChange} value={data.email}/>
                        <input name= "password" type="text" placeholder='Password'onChange={handleChange} value={data.password} />
                        <input name= "passwordCon" type="text" placeholder='Confirm Password' onChange={handleChange} value={data.passwordCon} />

                        <button type='submit'>Register User</button>
                </form>
            </div>
        </div>
    )
}

export default Register
